import "./ExpenseForm.css";
import { useState } from "react";

const ExpenseForm = () => {
    var [title, setTitle] = useState("")
    var [amount, setAmount] = useState("")
    var [newDate, setNewDate] = useState("")

    // const [userInput, setUserInput] =  useState({
    //     title: "",
    //     amount: "",
    //     newDate: ""
    // })

    const onTitleChange = (event) => {
        console.log(setTitle(
            event.target.value
            ));
    }
    const onAmountChange = (event) => {
        console.log(setAmount(
            event.target.value
            ));
    }
    const onDateChange = (event) => {
        console.log(setNewDate(
            event.target.value
            ));
    }


    const onSubmitHandler = (event) => {
        event.preventDefault();
        const expenseData = {
            newTitle : title,
            newAmount: amount,
            date : new Date(newDate)
        }
        console.log(expenseData);
        // setTitle(event.target.value);
        // console.log(title)
        
        setTitle = "";
        setAmount = "";
        setNewDate = "";
    }


    return(

    <form onSubmit={onSubmitHandler}>
        <div className="new-expense__controls">
            <div className="new-expense__controls">
                <label>Title</label>
                <input type="text" value={setTitle} onChange={onTitleChange}/>
            </div>
            <div className="new-expense__controls">
                <label>Amount</label>
                <input type="number" value={setAmount} onChange={onAmountChange}/>
            </div>
            <div className="new-expense__controls">
                <label>Date</label>
                <input type="date" min="2019-01-01" max="2022-01-01" value={setNewDate} onChange={onDateChange}/>
            </div>
        </div>
        <button className="new-expense__actions" type="submit">Add Expense</button>

    </form>
    );
}

export default ExpenseForm;
