import ExpenseItem from "./ExpensesItem";
import "./Expenses.css"
import Card from "../UI/Card";
import YearFilter from "./YearFilter";
import {useState} from "react"
import Button from "../UI/Button"

function Expenses(props) {
    const [selectedYear, setselectedYear] = useState("2023")

    const onYearSelectHander= (selected) => {
        setselectedYear(selected);
    }
    const filteredtems = props.items.filter(
        (x) => {
         return    x.date.getFullYear().toString() === selectedYear 
        }
    )
    const showAll = () => {
        
    }
   
    return (
        <Card className="expenses">
            <YearFilter selected = {selectedYear} onYearSelect = {onYearSelectHander}/>
            {filteredtems.length === 0 ? <p>No Expenses Selected</p> : filteredtems.map((expensesDone) => (<ExpenseItem key = {expensesDone.id} title = {expensesDone.newTitle} amount = {expensesDone.newAmount} date = {expensesDone.date}></ExpenseItem>))}
            <Button btntext = "{showAll}"/>
            
           

            
            {/* <ExpenseItem title={props.items[0].title} amount={props.items[0].amount} date={props.items[0].date}></ExpenseItem>
            <ExpenseItem title={props.items[1].title} amount={props.items[1].amount} date={props.items[1].date}></ExpenseItem>
            <ExpenseItem title={props.items[2].title} amount={props.items[2].amount} date={props.items[2].date}></ExpenseItem>
            <ExpenseItem title={props.items[3].title} amount={props.items[3].amount} date={props.items[3].date}></ExpenseItem> */}

        </Card>
    )
}


export default Expenses;
